import { createSlice } from '@reduxjs/toolkit';

export const notificationSlice = createSlice({
    name: 'notifications',
    initialState: {
        notificationModal: false,
        notifications: []
    },
    reducers: {
        createNotification: (state) => {

            state.notifications.push([]);
        },
        deleteNotification: (state, notificationId) => {
            state.notifications.filter((item) => {
                item.id != notificationId;
            });
        },
        editNotification: (state, notificationId) => {

        }
    }
});

export const { createNotification, deleteNotification, editNotification } = notificationSlice.actions

export default notificationSlice.reducer
