import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import TextField from '@mui/material/TextField';

import { TimePicker } from '@mui/x-date-pickers/TimePicker';

import { useSelector, useDispatch } from 'react-redux';
import { changeModalState, changeCreationModalState} from '../reducers/notificationSlice';

export default function CalendarNotificationForm() {
    const open = useSelector((state) => state.notifications.notificationCreationModalState);
    const monthObject = useSelector((state) => state.notifications.monthDay);
    const monthName = useSelector((state) => state.date.monthName);
    const year = useSelector((state) => state.date.year);

    const [value, setValue] = React.useState<Date | null>(
        new Date('2014-08-18T21:11:54')
    );

    const handleChange = (newValue: Date | null) => {
        setValue(newValue);
    };

    const dispatch = useDispatch();

    const handleClickOpen = () => {
        dispatch(changeCreationModalState(""));
    };

    const handleClose = () => {
        dispatch(changeCreationModalState(""));
    };

    return (
        <div>
            <Button variant="outlined" onClick={handleClickOpen}>
            Open form dialog
            </Button>
            <Dialog
            open={open}
            onClose={handleClose}>
            <DialogTitle>{"New reminder"} </DialogTitle>
            <DialogContent>
                <TextField id="standard-basic" label="Notification" variant="standard" />
                <TimePicker
                    label="Time"
                    value={value}
                    onChange={handleChange}
                    renderInput={(params) => <TextField {...params} />}
                />
            </DialogContent>
            <DialogActions>
                <Button onClick={handleClose}>Cancel</Button>
                <Button onClick={handleClose}>Create Notification</Button>
            </DialogActions>
            </Dialog>
        </div>
    );
}
