import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';


import Avatar from '@mui/material/Avatar';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import IconButton from '@mui/material/IconButton';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

import { useSelector, useDispatch } from 'react-redux';
import { changeModalState, changeCreationModalState } from '../reducers/notificationSlice';


function generate(element: React.ReactElement) {
    return [0, 1, 2].map((value) =>
        React.cloneElement(element, {
            key: value
        })
    );
}

export default function CalendarNotifications() {
    const open = useSelector((state) => state.notifications.notificationModalState);
    const monthObject = useSelector((state) => state.notifications.monthDay);
    const monthName = useSelector((state) => state.date.monthName);
    const year = useSelector((state) => state.date.year);
    const [secondary, setSecondary] = React.useState(false);

    const dispatch = useDispatch();

    const handleClickOpen = () => {
        dispatch(changeModalState(""));
    };

    const handleClose = () => {
        dispatch(changeModalState(""));
    };

    const handleOpenForm = () => {
        dispatch(changeCreationModalState(""));
    }

    return (
    <div>
        <Button variant="outlined" onClick={handleClickOpen}>
        Open form dialog
        </Button>
        <Dialog
          open={open}
          onClose={handleClose}>
        <DialogTitle>{"Notifications - " + monthObject.payload.monthDay + " of " + monthName + " of " + year} </DialogTitle>
        <DialogContent>
            <List sx={{ width: '100%', bgcolor: 'background.paper' }}>
              {generate(

                <ListItem
                  button
                  onClick={handleOpenForm}
                  secondaryAction={
                    <IconButton 
                      onClick={handleDeleteClick}
                      edge="end" aria-label="delete">
                      <DeleteIcon />
                    </IconButton>
                  }>
                  <ListItemText
                    primary="Single-line item"
                  />
                </ListItem>
              )}
            </List>
        </DialogContent>
        <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
            <Button onClick={handleOpenForm}>Create Reminder</Button>
        </DialogActions>
        </Dialog>
    </div>
    );
}
