            });*/
        },
        editNotification: (state, notificationId) => {

        },
        changeModalState: (state, currentDay) => {
            debugger;
            state.currentDay = currentDay;
            state.notificationModalState = !state.notificationModalState;
        },
        changeCreationModalState: (state, currentDay) => {
            state.currentDay = currentDay;
            state.notificationModalState = false;
            state.notificationCreationModalState = !state.notificationCreationModalState;
        }
    }
});

export const { createNotification, deleteNotification, editNotification, changeModalState, changeCreationModalState } = notificationSlice.actions

export default notificationSlice.reducer
