import React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';


const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    fontWeight: '600',
    height: 'calc((100vh - 144px)/7)',
    '&:hover':{
      '&>div':{
        color:'green'
      }
    },
    color: theme.palette.text.secondary
}));

const DayItem = styled(Paper)(({ theme }) => ({
    backgroundColor: 'rgb(25, 118, 210)',
    fontWeight: '600',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    height: '1',
    '&:hover':{
      '&>a':{
        color:'green'
      }
    },
    color: 'white'
}));

function FormRow(props) {
    return (
      <React.Fragment>
        <Grid item xs={0.8}>
          <DayItem elevation={6}>{props.weekDay}</DayItem>
        </Grid>
        <Grid item xs={1.7}>
          <Item elevation={6}>Item</Item>
        </Grid>
        <Grid item xs={1.7}>
          <Item elevation={6}>Item</Item>
        </Grid>
        <Grid item xs={1.7}>
          <Item elevation={6}>Item</Item>
        </Grid>
        <Grid item xs={1.7}>
          <Item elevation={6}>Item</Item>
        </Grid>
        <Grid item xs={1.7}>
          <Item elevation={6}>Item</Item>
        </Grid>
        <Grid item xs={1.7}>
          <Item elevation={6}>Item</Item>
        </Grid>
      </React.Fragment>
    );
  }

function CalendarMonth(props) {
  return (
    <Box sx={{
      width: '100%',
      height: '100%'
    }}>
        <Grid
            container
            spacing={0.5}
            direction="row">

            <Grid
                container
                xs={1.7}
                direction="column"
                item spacing={0.5}>
                <FormRow
                    weekDay="Sunday"/>
            </Grid>
            <Grid
                container
                xs={1.7}
                direction="column"
                item spacing={0.5}>
                <FormRow
                    weekDay="Monday"/>
            </Grid>
            <Grid
                container
                xs={1.7}
                direction="column"
                item spacing={0.5}>
                <FormRow
                    weekDay="Tuesday"/>
            </Grid>
            <Grid
                container
                xs={1.7}
                direction="column"
                item spacing={0.5}>
                <FormRow
                    weekDay="Wednesday"/>
            </Grid>
            <Grid
                container
                xs={1.7}
                direction="column"
                item spacing={0.5}>
                <FormRow
                    weekDay="Thursday"/>
            </Grid>
            <Grid
                container
                xs={1.7}
                direction="column"
                item spacing={0.5}>
                <FormRow
                    weekDay="Friday"/>
            </Grid>
            <Grid
                container
                xs={1.7}
                direction="column"
                item spacing={0.5}>
                <FormRow
                    weekDay="Saturday"/>
            </Grid>
        </Grid>
    </Box>
  )
}

export default CalendarMonth;
