import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import TextField from '@mui/material/TextField';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { CountryDropdown, RegionDropdown, CountryRegionData } from 'react-country-region-selector';
import { Country, State, City }  from 'country-state-city';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';

import Autocomplete from '@mui/material/Autocomplete';
import { useSelector, useDispatch } from 'react-redux';
import { createNotification, changeCreationModalState, clearEditingMode } from '../reducers/notificationSlice';
import { DescriptionOutlined } from '@mui/icons-material';

export default function CalendarNotificationForm() {
    const dispatch = useDispatch();
    const monthObject = useSelector((state) => state.notifications.monthObject);
    const open = useSelector((state) => state.notifications.notificationCreationModalState);
    const isEditing = useSelector((state) => state.notifications.isEditing);
    const reminderToEdit = useSelector((state) => state.notifications.reminderToEdit);
    const [country, selectCountry] =  React.useState('');
    const date = React.useRef(new Date());
    const [cities, setCities] = React.useState([]);
    const description = React.useRef('');
    const city = React.useRef('');
    const countries = Country.getAllCountries();

    if (isEditing) {
        date.current = new Date(reminderToEdit.payload.date);
        city.current = (reminderToEdit.payload.city);
        description.current = (reminderToEdit.payload.description);
    }

    const handleDateChange = (newValue) => {
        date.current = (newValue);
    };

    const handleCountryChange = (val) => {
        selectCountry(val);
        let country = countries.filter(function(country) {
            return country.name == val;
        });
        let cities = City.getCitiesOfCountry(country[0].isoCode);
        setCities(cities);
    };

    const handleClickOpen = () => {
        dispatch(changeCreationModalState(""));
    };

    const handleClose = () => {
        dispatch(changeCreationModalState(""));
    };

    const handleTextChange = (event) => {
        description.current = (event.currentTarget.value);
    };

    const createReminder = () => {
        let notificationObj = {
            'date': date.current,
            'city': city.current,
            'description': description.current ? description.current : ""
        };

        dispatch(createNotification(notificationObj));
    }

    return (
        <div>
            <Button variant="outlined" onClick={handleClickOpen}>
            Open form dialog
            </Button>
            <Dialog
            open={open}
            onClose={handleClose}>
            <DialogTitle>{isEditing ? "Edit reminder": "New reminder"} </DialogTitle>
            <DialogContent>
                <Box sx={{ display: "flex", flexDirection: "column", gap: "20px" }}>
                    <TextField
                        sx={{ minWidth: "300px" }}
                        inputProps={{ maxLength: 30 }}
                        value={description.current}
                        onChange={handleTextChange}
                        id="standard-basic"
                        label="Reminder"
                        variant="standard" />

                    <LocalizationProvider dateAdapter={AdapterDateFns}>
                        <DateTimePicker
                            sx={{ minWidth: "300px" }}
                            label="Time"
                            value={date.current}
                            onChange={handleDateChange}
                            renderInput={(params) => <TextField {...params} />}
                        />
                    </LocalizationProvider>
                    <CountryDropdown
                        sx={{ minWidth: "300px" }}
                        value={country}
                        onChange={handleCountryChange} />
                    <Autocomplete
                        sx={{ minWidth: "300px" }}
                        onChange={(event, value) => city.current = (value)}
                        disablePortal
                        value={city.current}
                        id="citySelection"
                        options={cities}
                        getOptionLabel={(option) => option.name}
                        renderInput={(params) => <TextField {...params} label="City" />}
                    />
                </Box>
            </DialogContent>
            <DialogActions>
                <Button onClick={handleClose}>Cancel</Button>
                <Button onClick={createReminder}>Create Reminder</Button>
            </DialogActions>
            </Dialog>
        </div>
    );
}
