export const notificationSlice = createSlice({
    name: 'notifications',
    initialState: {
        notificationModalState: false,
        notifications: [],
        monthDay: ''
    },
    reducers: {
        createNotification: (state) => {

            state.notifications.push([]);
        },
        deleteNotification: (state, notificationId) => {
            state.notifications.filter((item) => {
                return (item.id != notificationId);
            });
        },
        editNotification: (state, notificationId) => {

        },
        changeModalState: (state, monthDay) => {
            state.monthDay = monthDay;
            state.notificationModalState = !state.notificationModalState;
