import * as React from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import { useSelector, useDispatch } from 'react-redux';
import { changeModalState } from '../reducers/notificationSlice';

export default function CalendarNotification() {
    const open = useSelector((state) => state.notifications.notificationModalState);
    const weekDay = useSelector((state) => state.notifications.notificationModalState);
    const dispatch = useDispatch();

    const handleClickOpen = () => {
        dispatch(changeModalState(""));
    };

    const handleClose = () => {
        dispatch(changeModalState(""));
    };

    return (
    <div>
        <Button variant="outlined" onClick={handleClickOpen}>
        Open form dialog
        </Button>
        <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Notifications - </DialogTitle>
        <DialogContent>
            <DialogContentText>
            To subscribe to this website, please enter your email address here. We
            will send updates occasionally.
            </DialogContentText>
            <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email Address"
            type="email"
            fullWidth
            variant="standard"
            />
        </DialogContent>
        <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
            <Button onClick={handleClose}>Subscribe</Button>
        </DialogActions>
        </Dialog>
    </div>
    );
}
